<?php/** * @component     CG RÃ©sa - Joomla 4.0.0 * Version			: 2.2.3 * @license http://www.gnu.org/licenses/gpl-2.0.html GNU/GPL * @copyright (c) 2022 ConseilGouz. All Rights Reserved. * @author ConseilGouz **/namespace ConseilGouz\Component\CGResa\Administrator\Helper;defined('_JEXEC') or die;use Joomla\Registry\Registry;use Joomla\CMS\Factory;use Joomla\CMS\Table\Table;use ConseilGouz\Component\CGResa\Administrator\Table\ConfigTable;class CGResaHelper{	public static $extension = 'com_cgresa';	public static $params;    public static function renderSideBar()    {		$data = array(			'config' => array(				array(				 	'label' => 'CGRESA_CONFIG',					'url'   => 'index.php?option=com_cgresa',					'icon'  => 'options',					'view'  => 'config'				)			)		);        // Render layout        $layout = new JLayoutFile('sidebar', JPATH_COMPONENT_ADMINISTRATOR . '/layouts');        return $layout->render($data);    }    /**     *  Returns permissions     *     *  @return  object     */    public static function getActions()    {        $user = Factory::getUser();        $result = new \StdClass;        $assetName = 'com_cgresa';        $actions = array(            'core.admin', 'core.manage', 'core.create', 'core.edit', 'core.edit.state', 'core.delete'        );        foreach ($actions as $action)        {            $result->set($action, $user->authorise($action, $assetName));        }        return $result;    }	}